mapper.update:
UPDATE movie SET
theaterNo=#{theaterNo}
WHERE mno=#{mno}

mapper.select:
SELECT tno,tname,tloc,tdate
FROM reserveTheater
WHERE tno=#{tno}

mapper.update:
UPDATE ReserveTheater SET
tdate=#{tdate}
WHERE tno=#{tno}

UPDATE ReserveDate SET
time=#{time}
WHERE tno=#{tno}

UPDATE reserve SET
isReserve=1
WHERE rno=#{rno}
